import React from 'react';
import './App.css';
import { useState } from "react";

type Note = {
  id: number;
  title: string;
  content: string;
};

const App = () => {
  const [notes, setNotes] = useState<Note[]>([
    {
      id: 1,
      title: "Umang",
      content: "Knowledge Graph!",
    },
    {
      id: 2,
      title: "Kushal",
      content: "Blockchain!",
    },
    {
      id: 3,
      title: "Vitthal",
      content: "Blockchain!",
    },
    {
      id: 4,
      title: "Ishaan",
      content: "React+API!",
    },
  ]);


  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [selectedNote, setSelectedNote] = useState<Note | null>(null);

  const handleNoteClick = (note: Note) => {
    setSelectedNote(note);
    setTitle(note.title);
    setContent(note.content);
  };

  const handleAddNote = (event: React.FormEvent) => {
    event.preventDefault();
    const newNote: Note = {
      id: notes.length + 1,
      title: title,
      content: content
    };

    setNotes([newNote, ...notes]);
    setTitle("");
    setContent("");
    setSelectedNote(null);
  };

  const handleUpdateNote = (
    event: React.FormEvent
  ) => {
    event.preventDefault();
    if(!selectedNote) {
      return;
    }

    const updatedNote: Note = {
      id: selectedNote.id,
      title: title,
      content: content,
    }

    const updatedNotesList = notes.map((note)=>
    note.id === selectedNote.id
    ? updatedNote
    : note
  )
  setNotes(updatedNotesList)
  setTitle("")
  setContent("")
  setSelectedNote(null);
};

const handleCancel = () => {
  setTitle("")
  setContent("")
  setSelectedNote(null);
}

const deleteNote = (
  event: React.MouseEvent, 
  noteId: number
) => {
  event.stopPropagation();

  const updatedNotes = notes.filter(
    (note) => note.id !== noteId
  )

  setNotes(updatedNotes);
}

return (
  <div className="app-container bg-gray-900 h-screen p-8 overflow-hidden fixed inset-0 flex flex-col">
    <form 
      className="note-form bg-gray-800 p-6 rounded-lg shadow-lg space-y-6 flex flex-col h-full"
      onSubmit={(event) =>
        selectedNote ? handleUpdateNote(event) : handleAddNote(event)
      }
    >
      <input
        className="w-full p-3 bg-gray-700 text-gray-300 placeholder-gray-500 rounded-lg border border-transparent focus:border-blue-500 focus:outline-none transition-colors"
        value={title}
        onChange={(event) => setTitle(event.target.value)}
        placeholder="Title"
        required
      />
      <textarea
        className="w-full p-3 bg-gray-700 text-gray-300 placeholder-gray-500 rounded-lg border border-transparent focus:border-blue-500 focus:outline-none transition-colors resize-none flex-grow"
        value={content}
        onChange={(event) => setContent(event.target.value)}
        placeholder="Content"
        required
      />

      {selectedNote ? (
        <div className="edit-buttons flex space-x-3">
          <button 
            type="submit" 
            className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg shadow-md"
          >
            Save
          </button>
          <button 
            className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg shadow-md"
            onClick={handleCancel}
          >
            Cancel
          </button>
        </div>
      ) : (
        <button 
          type="submit" 
          className="w-full py-3 bg-gradient-to-r from-blue-500 to-green-500 hover:from-blue-600 hover:to-green-600 text-white rounded-lg shadow-lg"
        >
          Add Note
        </button>
      )}
    </form>

    <div className="notes-grid grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-x-6 gap-y-6 mt-8 overflow-y-auto flex-grow">
      {notes.map((note) => (
      <div
      className="note-item bg-gray-800 p-6 rounded-lg shadow-lg hover:shadow-2xl transition-shadow cursor-pointer relative h-[200px] overflow-hidden"
      onClick={() => handleNoteClick(note)}
      key={note.id}
      >
      <div className="notes-header flex justify-end">
      <button 
        className="text-red-500 hover:text-red-600"
        onClick={(event) => deleteNote(event, note.id)}
      >
        x
      </button>
      </div>
      <h2 className="text-xl font-bold text-blue-400">{note.title}</h2>
      <p className="text-darkgrey-300 mt-2 line-clamp-4">{note.content}</p>
      </div>
      ))}
    </div>
  </div>
);



}

export default App;
