import React from 'react';
import './App.css';
import { useState } from "react";

// Define the extended Note type
type Note = {
  id: number;
  patientID: string;
  dateOfBirth: string;
  firstName: string;
  lastName: string;
  middleName: string;
  emergencyContactName: string;
  emergencyContactRelationship: string;
  emergencyContactPhone: string;
  emergencyContactEmail: string;
  insuranceProvider: string;
  policyNumber: string;
  coverageLimit: string;
  coPay: string;
  allergies: string[];
  chronicConditions: string[];
  surgeries: string[];
  familyMedicalHistory: string[];
  pastHospitalizations: string[];
  gender: string;
  primaryPhysicianName: string;
  primaryPhysicianContact: string;
  primaryPhysicianEmail: string;
  visitRecords: string[];
  currentMedications: string[];
  contactEmail: string;
  contactPhone: string;
  contactAddress: string;
};

const App = () => {
  const [notes, setNotes] = useState<Note[]>([
    {
      id: 1,
      patientID: "unique-patient-id-96",
      dateOfBirth: "1952-11-27",
      firstName: "Dennis",
      lastName: "V May",
      middleName: "",
      emergencyContactName: "William Webb",
      emergencyContactRelationship: "Sibling",
      emergencyContactPhone: "3564168487",
      emergencyContactEmail: "mckenziechase@example.org",
      insuranceProvider: "Little, Smith and Ortega",
      policyNumber: "H2175139308",
      coverageLimit: "$487672",
      coPay: "$16",
      allergies: ["Peanuts", "Shellfish"],
      chronicConditions: ["Diabetes"],
      surgeries: ["Cataract Surgery (2017-10-25)"],
      familyMedicalHistory: ["Father: Heart Disease"],
      pastHospitalizations: ["Fracture (2023-09-13, 2 days)"],
      gender: "Male",
      primaryPhysicianName: "Dr. Ruth Price",
      primaryPhysicianContact: "(660) 553-5609x2422",
      primaryPhysicianEmail: "oadams@example.com",
      visitRecords: ["Visit ID: visit-126, 2024-08-02, Consultation"],
      currentMedications: ["Lisinopril, 498mg, Once daily"],
      contactEmail: "james89@example.org",
      contactPhone: "637.498.7284x236",
      contactAddress: "3130 Bradley Estate Apt. 875, Hernandezport, CO, 31816",
    },
  ]);

  const [formData, setFormData] = useState<Note>({
    id: notes.length + 1,
    patientID: "",
    dateOfBirth: "",
    firstName: "",
    lastName: "",
    middleName: "",
    emergencyContactName: "",
    emergencyContactRelationship: "",
    emergencyContactPhone: "",
    emergencyContactEmail: "",
    insuranceProvider: "",
    policyNumber: "",
    coverageLimit: "",
    coPay: "",
    allergies: [],
    chronicConditions: [],
    surgeries: [],
    familyMedicalHistory: [],
    pastHospitalizations: [],
    gender: "",
    primaryPhysicianName: "",
    primaryPhysicianContact: "",
    primaryPhysicianEmail: "",
    visitRecords: [],
    currentMedications: [],
    contactEmail: "",
    contactPhone: "",
    contactAddress: "",
  });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAddNote = (event: React.FormEvent) => {
    event.preventDefault();
    const newNote: Note = { ...formData, id: notes.length + 1 };
    setNotes([newNote, ...notes]);
    setFormData({
      id: notes.length + 1,
      patientID: "",
      dateOfBirth: "",
      firstName: "",
      lastName: "",
      middleName: "",
      emergencyContactName: "",
      emergencyContactRelationship: "",
      emergencyContactPhone: "",
      emergencyContactEmail: "",
      insuranceProvider: "",
      policyNumber: "",
      coverageLimit: "",
      coPay: "",
      allergies: [],
      chronicConditions: [],
      surgeries: [],
      familyMedicalHistory: [],
      pastHospitalizations: [],
      gender: "",
      primaryPhysicianName: "",
      primaryPhysicianContact: "",
      primaryPhysicianEmail: "",
      visitRecords: [],
      currentMedications: [],
      contactEmail: "",
      contactPhone: "",
      contactAddress: "",
    });
  };

  return (
    <div className="app-container bg-gray-900 h-screen p-8 overflow-hidden fixed inset-0 flex flex-col">
      <form className="note-form bg-gray-800 p-6 rounded-lg shadow-lg space-y-6 flex flex-col h-full" onSubmit={handleAddNote}>
        <input
          className="w-full p-3 bg-gray-700 text-gray-300 placeholder-gray-500 rounded-lg"
          name="patientID"
          value={formData.patientID}
          onChange={handleInputChange}
          placeholder="Patient ID"
          required
        />
        <input
          className="w-full p-3 bg-gray-700 text-gray-300 placeholder-gray-500 rounded-lg"
          name="firstName"
          value={formData.firstName}
          onChange={handleInputChange}
          placeholder="First Name"
          required
        />
        <input
          className="w-full p-3 bg-gray-700 text-gray-300 placeholder-gray-500 rounded-lg"
          name="lastName"
          value={formData.lastName}
          onChange={handleInputChange}
          placeholder="Last Name"
          required
        />
        {/* Repeat similar input fields for the other details */}
        <button type="submit" className="w-full py-3 bg-blue-600 text-white rounded-lg shadow-lg">
          Add Note
        </button>
      </form>

      <div className="notes-grid grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-x-6 gap-y-6 mt-8 overflow-y-auto flex-grow">
        {notes.map((note) => (
          <div key={note.id} className="note-item bg-gray-800 p-6 rounded-lg shadow-lg">
            <h2 className="text-xl font-bold text-blue-400">{note.firstName} {note.lastName}</h2>
            <p>Patient ID: {note.patientID}</p>
            <p>DOB: {note.dateOfBirth}</p>
            <p>Gender: {note.gender}</p>
            <p>Emergency Contact: {note.emergencyContactName} ({note.emergencyContactRelationship})</p>
            <p>Insurance: {note.insuranceProvider}</p>
            {/* Add additional fields to display other details */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
