import React from 'react';
import './App.css';
import { useState } from "react";

type Note = {
  id: number;
  title: string;
  content: string;
};

const App = () => {
  const [notes, setNotes] = useState<Note[]>([
    {
      id: 1,
      title: "Umang",
      content: "Knowledge Graph!",
    },
    {
      id: 2,
      title: "Kushal",
      content: "Blockchain!",
    },
    {
      id: 3,
      title: "Vittal",
      content: "Blockchain!",
    },
    {
      id: 4,
      title: "Ishaan",
      content: "React+API!",
    },
  ]);


  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [selectedNote, setSelectedNote] = useState<Note | null>(null);

  const handleNoteClick = (note: Note) => {
    setSelectedNote(note);
    setTitle(note.title);
    setContent(note.content);
  };

  const handleAddNote = (event: React.FormEvent) => {
    event.preventDefault();
    const newNote: Note = {
      id: notes.length + 1,
      title: title,
      content: content
    };

    setNotes([newNote, ...notes]);
    setTitle("");
    setContent("");
    setSelectedNote(null);
  };

  const handleUpdateNote = (
    event: React.FormEvent
  ) => {
    event.preventDefault();
    if(!selectedNote) {
      return;
    }

    const updatedNote: Note = {
      id: selectedNote.id,
      title: title,
      content: content,
    }

    const updatedNotesList = notes.map((note)=>
    note.id === selectedNote.id
    ? updatedNote
    : note
  )
  setNotes(updatedNotesList)
  setTitle("")
  setContent("")
  setSelectedNote(null);
};

const handleCancel = () => {
  setTitle("")
  setContent("")
  setSelectedNote(null);
}

const deleteNote = (
  event: React.MouseEvent, 
  noteId: number
) => {
  event.stopPropagation();

  const updatedNotes = notes.filter(
    (note) => note.id !== noteId
  )

  setNotes(updatedNotes);
}

return (
  <div className="app-container bg-gradient-to-br from-blue-900 via-purple-900 to-black h-screen p-8 overflow-hidden fixed inset-0 flex flex-col relative">
    {/* Floating shapes for background effect */}
    <div className="absolute top-0 left-0 w-[300px] h-[300px] bg-gradient-to-r from-purple-500 to-blue-500 rounded-full blur-3xl opacity-30"></div>
    <div className="absolute bottom-0 right-0 w-[400px] h-[400px] bg-gradient-to-t from-pink-500 to-red-500 rounded-full blur-2xl opacity-30"></div>

    {/* Form Section */}
    <form
      className="note-form bg-gradient-to-br from-gray-900 via-gray-800 to-black p-8 rounded-3xl shadow-2xl space-y-8 flex flex-col h-full relative z-10 border border-purple-700"
      onSubmit={(event) =>
        selectedNote ? handleUpdateNote(event) : handleAddNote(event)
      }
    >
      <input
        className="w-full p-4 bg-gradient-to-br from-gray-800 via-gray-700 to-gray-900 text-gray-300 placeholder-gray-400 rounded-xl border border-transparent focus:border-purple-500 focus:outline-none transition-colors shadow-lg"
        value={title}
        onChange={(event) => setTitle(event.target.value)}
        placeholder="Note Title"
        required
      />
      <textarea
        className="w-full p-4 bg-gradient-to-br from-gray-800 via-gray-700 to-gray-900 text-gray-300 placeholder-gray-400 rounded-xl border border-transparent focus:border-purple-500 focus:outline-none transition-colors resize-none flex-grow shadow-lg"
        value={content}
        onChange={(event) => setContent(event.target.value)}
        placeholder="Write your content here..."
        required
      />

      {/* Conditional Buttons */}
      {selectedNote ? (
        <div className="edit-buttons flex space-x-4">
          <button
            type="submit"
            className="px-5 py-3 bg-gradient-to-r from-purple-500 via-blue-500 to-cyan-500 hover:from-purple-600 hover:via-blue-600 hover:to-cyan-600 text-white rounded-lg shadow-lg"
          >
            Save
          </button>
          <button
            className="px-5 py-3 bg-gradient-to-r from-red-500 via-orange-500 to-pink-500 hover:from-red-600 hover:via-orange-600 hover:to-pink-600 text-white rounded-lg shadow-lg"
            onClick={handleCancel}
          >
            Cancel
          </button>
        </div>
      ) : (
        <button
          type="submit"
          className="w-full py-4 bg-gradient-to-r from-green-500 via-teal-500 to-cyan-500 hover:from-green-600 hover:via-teal-600 hover:to-cyan-600 text-white rounded-2xl shadow-2xl"
        >
          Add Note
        </button>
      )}
    </form>

    {/* Notes Grid */}
    <div className="notes-grid grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-x-8 gap-y-8 mt-10 overflow-y-auto flex-grow relative z-10">
      {notes.map((note) => (
        <div
          className="note-item bg-gradient-to-br from-black via-gray-900 to-gray-800 p-6 rounded-2xl shadow-2xl hover:shadow-[0_10px_20px_rgba(144,101,246,0.8)] transition-shadow cursor-pointer relative h-[220px] overflow-hidden border border-purple-700"
          onClick={() => handleNoteClick(note)}
          key={note.id}
        >
          <div className="notes-header flex justify-between">
            <h2 className="text-xl font-extrabold bg-gradient-to-r from-blue-500 via-purple-400 to-blue-500 bg-clip-text text-transparent">
              {note.title}
            </h2>
            <button
              className="text-red-500 hover:text-red-700"
              onClick={(event) => deleteNote(event, note.id)}
            >
              x
            </button>
          </div>
          <p className="text-gray-300 mt-2 line-clamp-4">{note.content}</p>
        </div>
      ))}
    </div>
  </div>
);




}

export default App;
