import React from 'react';
import './App.css';
import { useState } from "react";

type Note = {
  id: number;
  name: string;
  allergies: string;
  chronicConditions: string;
  surgeries: string;
  currentMedication: string;
  dosage: string;
  frequency: string;
};

const App = () => {
  const [notes, setNotes] = useState<Note[]>([]);
  const [name, setName] = useState("");
  const [allergies, setAllergies] = useState("");
  const [chronicConditions, setChronicConditions] = useState("");
  const [surgeries, setSurgeries] = useState("");
  const [currentMedication, setCurrentMedication] = useState("");
  const [dosage, setDosage] = useState("");
  const [frequency, setFrequency] = useState("");
  const [selectedNote, setSelectedNote] = useState<Note | null>(null);

  const handleNoteClick = (note: Note) => {
    setSelectedNote(note);
    setName(note.name);
    setAllergies(note.allergies);
    setChronicConditions(note.chronicConditions);
    setSurgeries(note.surgeries);
    setCurrentMedication(note.currentMedication);
    setDosage(note.dosage);
    setFrequency(note.frequency);
  };

  const handleAddNote = (event: React.FormEvent) => {
    event.preventDefault();
    const newNote: Note = {
      id: notes.length + 1,
      name: name,
      allergies: allergies,
      chronicConditions: chronicConditions,
      surgeries: surgeries,
      currentMedication: currentMedication,
      dosage: dosage,
      frequency: frequency
    };

    setNotes([newNote, ...notes]);
    clearForm();
  };

  const handleUpdateNote = (event: React.FormEvent) => {
    event.preventDefault();
    if (!selectedNote) {
      return;
    }

    const updatedNote: Note = {
      id: selectedNote.id,
      name: name,
      allergies: allergies,
      chronicConditions: chronicConditions,
      surgeries: surgeries,
      currentMedication: currentMedication,
      dosage: dosage,
      frequency: frequency,
    };

    const updatedNotesList = notes.map((note) =>
      note.id === selectedNote.id ? updatedNote : note
    );
    setNotes(updatedNotesList);
    clearForm();
  };

  const clearForm = () => {
    setName("");
    setAllergies("");
    setChronicConditions("");
    setSurgeries("");
    setCurrentMedication("");
    setDosage("");
    setFrequency("");
    setSelectedNote(null);
  };

  const handleCancel = () => {
    clearForm();
  };

  const deleteNote = (event: React.MouseEvent, noteId: number) => {
    event.stopPropagation();
    const updatedNotes = notes.filter((note) => note.id !== noteId);
    setNotes(updatedNotes);
  };

  return (
    <div className="app-container bg-gray-900 h-screen p-8 overflow-hidden fixed inset-0 flex flex-col">
      <form
        className="note-form bg-gray-800 p-6 rounded-lg shadow-lg space-y-6 flex flex-col h-full"
        onSubmit={(event) =>
          selectedNote ? handleUpdateNote(event) : handleAddNote(event)
        }
      >
        <input
          className="w-full p-3 bg-gray-700 text-gray-300 placeholder-gray-500 rounded-lg border border-transparent focus:border-blue-500 focus:outline-none transition-colors"
          value={name}
          onChange={(event) => setName(event.target.value)}
          placeholder="Name"
          required
        />
        <input
          className="w-full p-3 bg-gray-700 text-gray-300 placeholder-gray-500 rounded-lg border border-transparent focus:border-blue-500 focus:outline-none transition-colors"
          value={allergies}
          onChange={(event) => setAllergies(event.target.value)}
          placeholder="Allergies"
          required
        />
        <input
          className="w-full p-3 bg-gray-700 text-gray-300 placeholder-gray-500 rounded-lg border border-transparent focus:border-blue-500 focus:outline-none transition-colors"
          value={chronicConditions}
          onChange={(event) => setChronicConditions(event.target.value)}
          placeholder="Chronic Conditions"
          required
        />
        <input
          className="w-full p-3 bg-gray-700 text-gray-300 placeholder-gray-500 rounded-lg border border-transparent focus:border-blue-500 focus:outline-none transition-colors"
          value={surgeries}
          onChange={(event) => setSurgeries(event.target.value)}
          placeholder="Surgeries"
          required
        />
        <input
          className="w-full p-3 bg-gray-700 text-gray-300 placeholder-gray-500 rounded-lg border border-transparent focus:border-blue-500 focus:outline-none transition-colors"
          value={currentMedication}
          onChange={(event) => setCurrentMedication(event.target.value)}
          placeholder="Current Medication"
          required
        />
        <input
          className="w-full p-3 bg-gray-700 text-gray-300 placeholder-gray-500 rounded-lg border border-transparent focus:border-blue-500 focus:outline-none transition-colors"
          value={dosage}
          onChange={(event) => setDosage(event.target.value)}
          placeholder="Dosage"
          required
        />
        <input
          className="w-full p-3 bg-gray-700 text-gray-300 placeholder-gray-500 rounded-lg border border-transparent focus:border-blue-500 focus:outline-none transition-colors"
          value={frequency}
          onChange={(event) => setFrequency(event.target.value)}
          placeholder="Frequency"
          required
        />

        {selectedNote ? (
          <div className="edit-buttons flex space-x-3">
            <button
              type="submit"
              className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg shadow-md"
            >
              Save
            </button>
            <button
              className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg shadow-md"
              onClick={handleCancel}
            >
              Cancel
            </button>
          </div>
        ) : (
          <button
            type="submit"
            className="w-full py-3 bg-gradient-to-r from-blue-500 to-green-500 hover:from-blue-600 hover:to-green-600 text-white rounded-lg shadow-lg"
          >
            Add Note
          </button>
        )}
      </form>

      <div className="notes-grid grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-x-6 gap-y-6 mt-8 overflow-y-auto flex-grow">
        {notes.map((note) => (
          <div
            className="note-item bg-gray-800 p-6 rounded-lg shadow-lg hover:shadow-2xl transition-shadow cursor-pointer relative h-[300px] overflow-hidden"
            onClick={() => handleNoteClick(note)}
            key={note.id}
          >
            <div className="notes-header flex justify-end">
              <button
                className="text-red-500 hover:text-red-600"
                onClick={(event) => deleteNote(event, note.id)}
              >
                x
              </button>
            </div>
            <h2 className="text-xl font-bold text-blue-400">{note.name}</h2>
            <p className="text-gray-300 mt-2">Allergies: {note.allergies}</p>
            <p className="text-gray-300 mt-2">Chronic Conditions: {note.chronicConditions}</p>
            <p className="text-gray-300 mt-2">Surgeries: {note.surgeries}</p>
            <p className="text-gray-300 mt-2">Medication: {note.currentMedication}</p>
            <p className="text-gray-300 mt-2">Dosage: {note.dosage}</p>
            <p className="text-gray-300 mt-2">Frequency: {note.frequency}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
